import PcHand from "./PcHand";
import Results from "./Results";
import React, { useState } from 'react';


const Janken = () => {

    const [showPcHand, setShowPcHand] = useState(false);
    const [clickedButton, setClickedButton] = useState(null);
    const [randomNumber, setRandomNumber] = useState(null);

    const handleClick = (buttonId) => {
        setShowPcHand(true);
        setClickedButton(buttonId);
    };

    return (
        <div className="Select">
            <div className="User">

                <div className="Gu" style={{ backgroundColor: clickedButton === 0 ? 'blue' : 'white', padding: '20px' }} onClick={() => handleClick(0)}>
                    <h1>✊</h1>

                </div>

                <div className="Cho" style={{ backgroundColor: clickedButton === 1 ? 'blue' : 'white', padding: '20px' }} onClick={() => handleClick(1)}>
                    <h1>✌️</h1>

                </div>

                <div className="Par" style={{ backgroundColor: clickedButton === 2 ? 'blue' : 'white', padding: '20px' }} onClick={() => handleClick(2)}>
                    <h1>✋</h1>

                </div>
            </div>

            <div className="Pc" >
                <p>PCの出した手は...</p>
                {showPcHand && <PcHand />}

            </div>

            <div className="Result">
            <p>結果は...</p>

            </div>

        </div>

    );


};
export default Janken;
