import PcHand from "./PcHand";
import UserCho from "./UserCho";
import UserGu from "./UserGu";
import UserPar from "./UserPar";
import React, { useState } from 'react';


const Janken = () => {

    const [showPcHand, setShowPcHand] = useState(false);
    const [bgColor, setBgColor] = useState('white');

    const handleClick = () => {
        setShowPcHand(true);
        setBgColor('blue');
      };

    return (
        <div className="Select">
            <div className="User">

                <div className="Gu" style={{ backgroundColor: bgColor, minHeight: '100px', padding: '20px' }} onClick={handleClick}>
                    <h1>✊</h1>

                </div>

                <div className="Cho"  style={{ backgroundColor: bgColor, minHeight: '100px', padding: '20px' }} onClick={handleClick}>
                    <h1>✌️</h1>

                </div>

                <div className="Par"  style={{ backgroundColor: bgColor, minHeight: '100px', padding: '20px' }} onClick={handleClick}>
                    <h1>✋</h1>

                </div>
            </div>

            <div className="Pc" >
<p>PCの出した手は...</p>
            {showPcHand && <PcHand />}

            </div>

        </div>

    );


};
export default Janken;
